addMyLabel <- function(map, lat, lng, label, color, direction = "bottom") {
  map |> 
    addLabelOnlyMarkers(
      lng = lng,
      lat = lat,
      label = label,
    labelOptions = labelOptions(noHide = T, direction = direction,
      style =list(
        "color" = color,
        "font-family" = "serif",
        "font-style" = "italic",
        "box-shadow" = "3px 3px rgba(0,0,0,0.25)",
        "font-size" = "14px",
        "border-color" = "rgba(0,0,0,0.5)"
      ))    
    )
}
